import React from 'react';
import { Experience } from '../../types/cv';

interface ExperienceSectionProps {
  experience: Experience[];
  onChange: (experience: Experience[]) => void;
}

const COMPANY_LIST = [
  "Google",
  "Microsoft",
  "Amazon",
  "Facebook",
  "Apple",
  "Netflix",
  "Tesla",
  "TCS",
  "Infosys",
  "Wipro",
  "Accenture",
  "IBM",
  "Capgemini",
  "Cognizant",
  "HCL Technologies",
  "Deloitte",
  "EY",
  "KPMG",
  "PwC",
  "Other"
];

const POSITION_LIST = [
  "Software Engineer",
  "Senior Software Engineer",
  "Team Lead",
  "Project Manager",
  "Product Manager",
  "Data Scientist",
  "Business Analyst",
  "Consultant",
  "Intern",
  "Researcher",
  "Designer",
  "QA Engineer",
  "DevOps Engineer",
  "System Architect",
  "Other"
];

const ExperienceSection: React.FC<ExperienceSectionProps> = ({
  experience,
  onChange,
}) => {
  const handleAdd = () => {
    onChange([
      ...experience,
      {
        company: '',
        position: '',
        startDate: '',
        endDate: '',
        description: '',
        achievements: [],
      },
    ]);
  };

  const handleChange = (index: number, field: keyof Experience, value: any) => {
    const newExperience = [...experience];
    newExperience[index] = {
      ...newExperience[index],
      [field]: value,
    };
    onChange(newExperience);
  };

  const handleAchievementAdd = (index: number) => {
    const newExperience = [...experience];
    newExperience[index] = {
      ...newExperience[index],
      achievements: [...newExperience[index].achievements, ''],
    };
    onChange(newExperience);
  };

  const handleAchievementChange = (
    expIndex: number,
    achievementIndex: number,
    value: string
  ) => {
    const newExperience = [...experience];
    newExperience[expIndex].achievements[achievementIndex] = value;
    onChange(newExperience);
  };

  const handleAchievementRemove = (expIndex: number, achievementIndex: number) => {
    const newExperience = [...experience];
    newExperience[expIndex].achievements = newExperience[
      expIndex
    ].achievements.filter((_, i) => i !== achievementIndex);
    onChange(newExperience);
  };

  const handleRemove = (index: number) => {
    onChange(experience.filter((_, i) => i !== index));
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-lg font-medium text-gray-900 mb-4">Work Experience</h2>
      <div className="space-y-6">
        {experience.map((exp, index) => (
          <div key={index} className="border border-gray-200 p-4 rounded-md">
            <div className="flex justify-end mb-2">
              <button
                type="button"
                onClick={() => handleRemove(index)}
                className="text-red-600 hover:text-red-800"
              >
                Remove
              </button>
            </div>
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Company
                </label>
                <select
                  value={exp.company}
                  onChange={(e) => handleChange(index, 'company', e.target.value)}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                >
                  <option value="">Select company...</option>
                  {COMPANY_LIST.map((company) => (
                    <option key={company} value={company}>{company}</option>
                  ))}
                </select>
                {exp.company === 'Other' && (
                  <input
                    type="text"
                    placeholder="Enter company name"
                    value={exp.companyOther || ''}
                    onChange={(e) => handleChange(index, 'company', e.target.value)}
                    className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                  />
                )}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Position
                </label>
                <select
                  value={exp.position}
                  onChange={(e) => handleChange(index, 'position', e.target.value)}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                >
                  <option value="">Select position...</option>
                  {POSITION_LIST.map((position) => (
                    <option key={position} value={position}>{position}</option>
                  ))}
                </select>
                {exp.position === 'Other' && (
                  <input
                    type="text"
                    placeholder="Enter position name"
                    value={exp.positionOther || ''}
                    onChange={(e) => handleChange(index, 'position', e.target.value)}
                    className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                  />
                )}
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Start Date
                  </label>
                  <input
                    type="month"
                    value={exp.startDate}
                    onChange={(e) =>
                      handleChange(index, 'startDate', e.target.value)
                    }
                    className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    End Date
                  </label>
                  <input
                    type="month"
                    value={exp.endDate}
                    onChange={(e) =>
                      handleChange(index, 'endDate', e.target.value)
                    }
                    className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                  />
                </div>
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm font-medium text-gray-700">
                  Description
                </label>
                <textarea
                  value={exp.description}
                  onChange={(e) =>
                    handleChange(index, 'description', e.target.value)
                  }
                  rows={3}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                />
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Key Achievements
                </label>
                {exp.achievements.map((achievement, achievementIndex) => (
                  <div key={achievementIndex} className="flex gap-2 mb-2">
                    <input
                      type="text"
                      value={achievement}
                      onChange={(e) =>
                        handleAchievementChange(
                          index,
                          achievementIndex,
                          e.target.value
                        )
                      }
                      className="flex-1 border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
                      placeholder="Enter an achievement"
                    />
                    <button
                      type="button"
                      onClick={() =>
                        handleAchievementRemove(index, achievementIndex)
                      }
                      className="text-red-600 hover:text-red-800"
                    >
                      Remove
                    </button>
                  </div>
                ))}
                <button
                  type="button"
                  onClick={() => handleAchievementAdd(index)}
                  className="mt-2 inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-primary-700 bg-primary-100 hover:bg-primary-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  Add Achievement
                </button>
              </div>
            </div>
          </div>
        ))}
        <button
          type="button"
          onClick={handleAdd}
          className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
        >
          Add Experience
        </button>
      </div>
    </div>
  );
};

export default ExperienceSection;
