import React, { useState } from 'react';
import { Toaster } from 'react-hot-toast';
import Header from './components/Header';
import CVForm from './components/CVForm';
import { CVData, CVGeneratorState } from './types/cv';

const initialState: CVGeneratorState = {
  data: {
    personalInfo: {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      location: '',
    },
    education: [],
    experience: [],
    skills: [],
    certifications: [],
  },
  isLoading: false,
  error: null,
  activeSection: 'personalInfo',
  selectedTemplate: 'modern',
};

function App() {
  const [state, setState] = useState<CVGeneratorState>(initialState);

  const handleDataChange = (newData: Partial<CVData>) => {
    setState(prev => ({
      ...prev,
      data: { ...prev.data, ...newData },
    }));
  };

  const handleGeneratePDF = async () => {
    setState(prev => ({ ...prev, isLoading: true }));
    try {
      // TODO: Implement PDF generation API call
      await new Promise(resolve => setTimeout(resolve, 2000)); // Simulated delay
      // Handle success
    } catch (error) {
      setState(prev => ({ ...prev, error: 'Failed to generate PDF' }));
    } finally {
      setState(prev => ({ ...prev, isLoading: false }));
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Toaster position="top-right" />
      <Header />
      <main className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <CVForm
            data={state.data}
            isLoading={state.isLoading}
            activeSection={state.activeSection}
            onDataChange={handleDataChange}
            onGeneratePDF={handleGeneratePDF}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
